export function dijkstra(grid, startNode, finishNode, stopNode){
  const visitedNodesInOrder = [[],[]];
  const unvisitedNodes = getAllNodes(grid)
  var pos = stopNode[0] ? 0 : 1
  while(pos <= 1){
    console.log('here')
    var endNode = pos===0 ? stopNode[1] : finishNode
    unvisitedNodes.forEach((node) => {
      if(node === startNode){
        node.distance = 0
      } else {
        node.distance = Infinity
      }
    })
    while(!!unvisitedNodes){
        sortNodesByDistance(unvisitedNodes, endNode)
        const closestNode = unvisitedNodes.shift();

        if(closestNode.isWall) continue;

        if(closestNode.distance === Infinity && pos === 0){
          break;
        } else if(closestNode.distance === Infinity && pos === 1){
          return visitedNodesInOrder
        }
        closestNode.isVisited = true;
        if(pos === 0){
          visitedNodesInOrder[0].push(closestNode)
        } else if(pos === 1){
          visitedNodesInOrder[1].push(closestNode)
        }

        if(closestNode === Node) return visitedNodesInOrder;
        if(closestNode === stopNode[1] && pos === 0){
          break;
        } else if(closestNode === finishNode && pos === 1){
          return visitedNodesInOrder
        }
        updateUnvisitedNeighbors(closestNode, grid);
    }
    pos++
  }
}

function sortNodesByDistance(unvisitedNodes) {
    unvisitedNodes.sort((nodeA, nodeB) => nodeA.distance - nodeB.distance);
}

function updateUnvisitedNeighbors(node, grid) {
    const unvisitedNeighbors = getUnvisitedNeighbors(node, grid);
    for (const neighbor of unvisitedNeighbors) {
      neighbor.distance = node.distance + 1;
      neighbor.previousNode = node;
    }
}

function getUnvisitedNeighbors(node, grid) {
    const neighbors = [];
    const {col, row} = node;
    if (row > 0) neighbors.push(grid[row - 1][col]);
    if (row < grid.length - 1) neighbors.push(grid[row + 1][col]);
    if (col > 0) neighbors.push(grid[row][col - 1]);
    if (col < grid[0].length - 1) neighbors.push(grid[row][col + 1]);
    return neighbors.filter(neighbor => !neighbor.isVisited);
}

function getAllNodes(grid) {
    const nodes = [];
    for (const row of grid) {
      for (const node of row) {
        nodes.push(node);
      }
    }
    return nodes;
}

// Backtracks from the finishNode to find the shortest path.
// Only works when called *after* the dijkstra method above.
export function getNodesInShortestPathOrder(finishNode, isStop=false) {
    const nodesInShortestPathOrder = [];
    let currentNode = finishNode;
    while (currentNode !== null) {
      if(isStop && currentNode.isTarget===true) break;
      //console.log(currentNode, currentNode !== null)
      nodesInShortestPathOrder.unshift(currentNode);
      currentNode = currentNode.previousNode;
    }
    return nodesInShortestPathOrder;
}
