import React, {Component} from 'react';
import Visualizer from './PathFindingVisualizer/Visualizer'
import {dijkstra, getNodesInShortestPathOrder} from '../algorithims/dijkstra';
import Navbar from './navComponents/Navbar';
import './mainContainer.css'

const START_NODE_ROW = 10;
const START_NODE_COL = 5;
const FINISH_NODE_ROW = 10;
const FINISH_NODE_COL = 45;

export default class MainContainer extends Component{
    constructor(props){
        super(props);
        this.state = {
            grid: [],
            mouseIsPressed: false,
            showNav: false,
            currItemDesc: 'Start node'
        }
    }

    componentDidMount() {
        const grid = renderGrid();
        this.setState({grid});
    }

    handleMouseDown = (row, col) => {
        const newGrid = getNewGridWithWallToggled(this.state.grid, row, col);
        this.setState({grid: newGrid, mouseIsPressed: true});
    }

    handleMouseEnter = (row, col) => {
        if (!this.state.mouseIsPressed) return;
        const newGrid = getNewGridWithWallToggled(this.state.grid, row, col);
        this.setState({grid: newGrid});
    }

    handleMouseUp = () => {
        this.setState({mouseIsPressed: false});
    }

    handleNav = () => {
      this.setState({showNav: !this.state.showNav})
    }

    //navBar functions
    handleItemDescChange = (e) => {
      console.log(e.target.children)
      //this.setState({showNav: })
    }

    animateDijkstra(visitedNodesInOrder, nodesInShortestPathOrder) {
        for (let i = 0; i <= visitedNodesInOrder.length; i++) {
          if (i === visitedNodesInOrder.length) {
            setTimeout(() => {
                this.animateShortestPath(nodesInShortestPathOrder);
            }, 10 * i);
            return;
            }
          setTimeout(() => {
            const node = visitedNodesInOrder[i];
            document.getElementById(`node-${node.row}-${node.col}`).className =
              'node node-visited';
            }, 10 * i);
        }
    }

    animateShortestPath(nodesInShortestPathOrder) {
        for (let i = 0; i < nodesInShortestPathOrder.length; i++) {
          setTimeout(() => {
            const node = nodesInShortestPathOrder[i];
            document.getElementById(`node-${node.row}-${node.col}`).className =
              'node node-shortest-path';
          }, 50 * i);
        }
    }

    visualizeDijkstra() {
        const {grid} = this.state;
        const startNode = grid[START_NODE_ROW][START_NODE_COL];
        const finishNode = grid[FINISH_NODE_ROW][FINISH_NODE_COL];
        const visitedNodesInOrder = dijkstra(grid, startNode, finishNode);
        const nodesInShortestPathOrder = getNodesInShortestPathOrder(finishNode);
        this.animateDijkstra(visitedNodesInOrder, nodesInShortestPathOrder);
    }


    render(){ 
        return(
            <div className="main-container">
                <button onClick={() => this.visualizeDijkstra()}>click</button>
                <button onClick={this.handleNav}>show nav</button>
                <div className="visualizer-container">
                    <Visualizer 
                        state={this.state} 
                        handleMouseDown={this.handleMouseDown} 
                        handleMouseEnter={this.handleMouseEnter} 
                        handleMouseUp={this.handleMouseUp}
                    />
                </div>
                <Navbar state={this.state} handleItemDescChange={this.handleItemDescChange} visualizeDijkstra={this.visualizeDijkstra}/>
            </div>
        )
    }
}

const renderGrid = () => {
    const grid = [];
    for (let row = 0; row < 20; row++) {
      const currentRow = [];
      for (let col = 0; col < 50; col++) {
        currentRow.push(createNode(col, row));
      }
      grid.push(currentRow);
    }
    return grid;
}

const createNode = (col, row) => {
    return {
      col,
      row,
      isStart: row === START_NODE_ROW && col === START_NODE_COL,
      isFinish: row === FINISH_NODE_ROW && col === FINISH_NODE_COL,
      distance: Infinity,
      isVisited: false,
      isWall: false,
      previousNode: null,
    };
  };

  const getNewGridWithWallToggled = (grid, row, col) => {
    const newGrid = grid.slice();
    const node = newGrid[row][col];
    const newNode = {
      ...node,
      isWall: !node.isWall,
    };
    newGrid[row][col] = newNode;
    return newGrid;
  };
