            }
        }

        function  updateUnvisitedNeighbours(row, col, stack, grid,currNode) {
            let next;
            if (row > 0) {
              next = grid[row - 1][col];
              if (!next.isVisited) {
                next.previousNode = currNode;
                stack.push(next);
              }
            }
            if (row < grid.length - 1) {
              next = grid[row + 1][col];
              if (!next.isVisited) {
                next.previousNode = currNode;
                stack.push(next);
              }
            }
            if (col < grid[0].length - 1) {
              next = grid[row][col + 1];
              if (!next.isVisited) {
                next.previousNode = currNode;
                stack.push(next);
              }
            }
            if (col > 0) {
              next = grid[row][col - 1];
              if (!next.isVisited) {
                next.previousNode = currNode;
                stack.push(next);
              }
            }
        }

        const grid = message.data[0]
        const startNode = message.data[1]
        const finishNode = message.data[2]
        const stopNode = message.data[3]
        const visitedNodesInOrder = depthFirstSearch(grid, startNode, finishNode, stopNode)

        postMessage(visitedNodesInOrder)
