import React, {Component} from 'react';
import './nav.css'
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';

export default class Navbar extends Component {

    render(){
        const showNav = this.props.state.showNav
        const items = ['Start', 'Target', 'Wall', 'Wheight', 'Stop']
        const algs = ["Dijkstra's algorithim", "A* search", "Greedy Best-first Search", "Swarm algorithim"]
        const currItem = this.props.state.currItemObj

        return(
            <div className={`nav-bar nav-${showNav ? 'active' : 'inactive'}`}>
                <div className="algs-container">
                    <div className="alg-selector">
                        <Tabs
                            orientation="vertical"
                            TabIndicatorProps={{style: {background:'black'}}}
                            value={this.props.state.currAlgTab}

                        >
                            <Tab value={0} className="alg-tab" label="Dijkstra's algorithim"/>
                            <Tab value={1} label="A* search"/>
                            <Tab value={2} label="Greedy Best-first Search" />
                            <Tab value={3} label="Swarm algorithim"/>
                            <Tab value={4} label="Convergent Swarm Algorithim"/>
                            <Tab value={5} label="Bidirectional Swarm Algorithim"/>
                            <Tab value={6} label="Breadth-first Search"/>
                            <Tab value={7} label="Depth-first Search"/>
                        </Tabs>
                    </div>
                    <div className="alg-card"></div>
                </div>
                <div className="items-container">
                    <div className="items-selector">
                        {items.map((item, i) => {
                            const isCurrItem = this.props.state.currItemDesc === item
                            return (
                                <a className={`item-btn item-btn-${isCurrItem ? 'active' : 'inactive'} ${i===0 ? 'first-btn' : ''} ${i===(items.length-1) ? 'last-btn' : ''}`} id={`${item}-btn`}
                                onClick={(e) => {this.props.handleItemChange(e, item)}}>
                                    <p className="item-btn-text">{item}</p>
                                </a>
                            );
                        })}
                    </div>
                    <div className="item-card">
                        <h2 className="item-title">{currItem.title}</h2>
                        <p className="item-description">{currItem.description}</p>
                    </div>
                </div>
            </div>
        )
    }
}
