import React, {Component} from 'react';
import Visualizer from './PathFindingVisualizer/Visualizer'
import {dijkstra, getNodesInShortestPathOrder} from '../algorithims/dijkstra';
import {aStar} from '../algorithims/aStar'
import { breadthFirstSearch } from '../algorithims/breadthFirstSearch';
import Navbar from './navComponents/Navbar';
import itemData from '../data/item-data'
import algData from '../data/alg-data'
import './mainContainer.css'

// const START_NODE_ROW = 10;
// const START_NODE_COL = 5;
// const FINISH_NODE_ROW = 10;
// const FINISH_NODE_COL = 45;

export default class MainContainer extends Component{
    constructor(props){
        super(props);
        this.state = {
            grid: [],
            mouseIsPressed: false,
            showNav: false,
            currItemDesc: 'Start',
            currItemObj: itemData[0],
            currAlgObj: algData[0],
            currAlgTab: 0,
            startNode: [10, 5],
            finishNode: [10, 45],
            startIsMoving: false
        }
    }

    componentDidMount() {
        const grid = renderGrid(this.state);
        this.setState({grid});
    }

    handleMouseDown = (row, col) => {
        var newGrid = []
        if(this.state.grid[row][col]){
          this.setState({startIsMoving: true})
          newGrid = this.state.grid
        } 
        else {
          newGrid = getNewGridWithWallToggled(this.state.grid, row, col);
        }
        this.setState({grid: newGrid, mouseIsPressed: true});
    }

    handleMouseEnter = (row, col) => {
        if (!this.state.mouseIsPressed) return;
        var newGrid = [];
        if(this.state.startIsMoving){
          newGrid = getNewGridWithStart(this.state.grid, row, col, this.state.startNode)
          this.setState({startNode: [row, col]})
        } else {
          newGrid = getNewGridWithWallToggled(this.state.grid, row, col);
        }
        this.setState({grid: newGrid});
    }

    handleMouseUp = (row, col) => {
        this.setState({mouseIsPressed: false, startIsMoving: false});
    }

    handleNav = () => {
      this.setState({showNav: !this.state.showNav})
    }

    //navBar functions
    handleItemDescChange = (e, item) => {
      var newItem = `${item}`
      itemData.map((_item) => {
        if(item === _item.id){this.setState({currItemDesc: newItem, currItemObj: _item})}
      })
    }

    handleTabs = (e, val) => {
      this.setState({currAlgTab: val, currAlgObj: algData[val]})
    }

    //animte dijkstra

    animateDijkstra(visitedNodesInOrder, nodesInShortestPathOrder) {
        for (let i = 0; i <= visitedNodesInOrder.length; i++) {
          if (i === visitedNodesInOrder.length) {
            setTimeout(() => {
                this.animateShortestPath(nodesInShortestPathOrder);
            }, 10 * i);
            return;
            }
          setTimeout(() => {
            const node = visitedNodesInOrder[i];
            document.getElementById(`node-${node.row}-${node.col}`).className =
              'node node-visited';
            }, 10 * i);
        }
    }

    visualizeDijkstra() {
        const {grid} = this.state;
        const startNode = this.state.startNode
        const finishNode = this.state.finishNode
        const _startNode = grid[startNode[0]][startNode[1]];
        const _finishNode = grid[finishNode[0]][finishNode[1]];
        const visitedNodesInOrder = dijkstra(grid, _startNode, _finishNode);
        const nodesInShortestPathOrder = getNodesInShortestPathOrder(_finishNode);
        this.animateDijkstra(visitedNodesInOrder, nodesInShortestPathOrder);
    }

    //animate aStar

    animateaStar(visitedNodesInOrder, nodesInShortestPathOrder) {
      for (let i = 0; i <= visitedNodesInOrder.length; i++) {
        if (i === visitedNodesInOrder.length) {
          setTimeout(() => {
              this.animateShortestPath(nodesInShortestPathOrder);
          }, 10 * i);
          return;
          }
        setTimeout(() => {
          const node = visitedNodesInOrder[i];
          document.getElementById(`node-${node.row}-${node.col}`).className =
            'node node-visited';
          }, 10 * i);
      }
    }

    visualizeaStar() {
      const {grid} = this.state;
      const startNode = this.state.startNode
      const finishNode = this.state.finishNode
      const _startNode = grid[startNode[0]][startNode[1]];
      const _finishNode = grid[finishNode[0]][finishNode[1]];
      const visitedNodesInOrder = aStar(grid, _startNode, _finishNode);
      const nodesInShortestPathOrder = getNodesInShortestPathOrder(_finishNode);
      this.animateaStar(visitedNodesInOrder, nodesInShortestPathOrder);
    }

    //animate breadth first search 

    animatebfs(visitedNodesInOrder, nodesInShortestPathOrder) {
      for (let i = 0; i <= visitedNodesInOrder.length; i++) {
        if (i === visitedNodesInOrder.length) {
          setTimeout(() => {
              this.animateShortestPath(nodesInShortestPathOrder);
          }, 10 * i);
          return;
          }
        setTimeout(() => {
          const node = visitedNodesInOrder[i];
          document.getElementById(`node-${node.row}-${node.col}`).className =
            'node node-visited';
          }, 10 * i);
      }
    }

    visualizbfs() {
      const {grid} = this.state;
      const startNode = this.state.startNode
      const finishNode = this.state.finishNode
      const _startNode = grid[startNode[0]][startNode[1]];
      const _finishNode = grid[finishNode[0]][finishNode[1]];
      const visitedNodesInOrder = breadthFirstSearch(grid, _startNode, _finishNode);
      const nodesInShortestPathOrder = getNodesInShortestPathOrder(_finishNode);
      this.animatebfs(visitedNodesInOrder, nodesInShortestPathOrder);
    }

    //finish animations

    animateShortestPath(nodesInShortestPathOrder) {
      for (let i = 0; i < nodesInShortestPathOrder.length; i++) {
        setTimeout(() => {
          const node = nodesInShortestPathOrder[i];
          document.getElementById(`node-${node.row}-${node.col}`).className =
            'node node-shortest-path';
        }, 50 * i);
      }
    }

    render(){ 
        return(
            <div className="main-container">
                <button onClick={() => this.visualizbfs()}>Breadth First Search</button>
                <button onClick={() => this.visualizeaStar()}>a*</button>
                <button onClick={() => this.visualizeDijkstra()}>Dijkstra</button>
                <button onClick={this.handleNav}>show nav</button>
                <div className="visualizer-container">
                    <Visualizer 
                        state={this.state} 
                        handleMouseDown={this.handleMouseDown} 
                        handleMouseEnter={this.handleMouseEnter} 
                        handleMouseUp={this.handleMouseUp}
                    />
                </div>
                <Navbar state={this.state} handleTabs={this.handleTabs} handleItemChange={this.handleItemDescChange} visualizeDijkstra={this.visualizeDijkstra}/>
            </div>
        )
    }
}

const renderGrid = (state) => {
    const grid = [];
    for (let row = 0; row < 26; row++) {
      const currentRow = [];
      for (let col = 0; col < 55; col++) {
        currentRow.push(createNode(col, row, state));
      }
      grid.push(currentRow);
    }
    return grid;
}

const createNode = (col, row, state) => {
    const startNode = state.startNode
    const finishNode = state.finishNode
    return {
      col,
      row,
      isStart: row === startNode[0] && col === startNode[1],
      isFinish: row === finishNode[0] && col === finishNode[1],
      distance: Infinity,
      isVisited: false,
      isWall: false,
      previousNode: null,
    };
  };

  const getNewGridWithStart = (grid, row, col, prevStart) => {
    console.log('here')
    const newGrid = grid.slice();
    const newStart = newGrid[row][col];
    const oldStart = newGrid[prevStart[0]][prevStart[1]]
    const oldNode = {
      ...oldStart,
      isStart: !oldStart.isStart
    }
    const newNode = {
      ...newStart,
      isStart: !newStart.isStart,
    };
    newGrid[row][col] = newNode;
    newGrid[prevStart[0]][prevStart[1]] = oldNode
    return newGrid;
  }

  const getNewGridWithWallToggled = (grid, row, col) => {
    const newGrid = grid.slice();
    const node = newGrid[row][col];
    const newNode = {
      ...node,
      isWall: !node.isWall,
    };
    newGrid[row][col] = newNode;
    return newGrid;
  };
