import React, {Component} from 'react';
import './nav.css'

export default class Navbar extends Component {

    render(){
        const showNav = this.props.state.showNav
        const items = ['Start', 'Target', 'Wall', 'Wheight', 'Bomb']
        const currItem = this.props.state.currItemObj

        return(
            <div className={`nav-bar nav-${showNav ? 'active' : 'inactive'}`}>
                <div className="algs-container">
                    <div className="alg-selector"></div>
                    <div classNamr="alg-card"></div>
                </div>
                <div className="items-container">
                    <div className="items-selector">
                        {items.map((item, i) => {
                            const isCurrItem = this.props.state.currItemDesc === item
                            return (
                                <a className={`item-btn item-btn-${isCurrItem ? 'active' : 'inactive'} ${i===0 ? 'first-btn' : ''} ${i===(items.length-1) ? 'last-btn' : ''}`} id={`${item}-btn`}
                                onClick={(e) => {this.props.handleItemChange(e, item)}}>
                                    <p className="item-btn-text">{item}</p>
                                </a>
                            );
                        })}
                    </div>
                    <div className="item-card">
                        <h2 className="item-title">{curr}</h2>
                    </div>
                </div>
            </div>
        )
    }
}
