  const stopNode = state.prevStop;
  return {
    col,
    row,
    isStart: row === startNode[0] && col === startNode[1],
    isFinish: row === finishNode[0] && col === finishNode[1],
    isStop: state.isThereStop ? row === stopNode[0] && col === stopNode[1] : null,
    distance: Infinity,
    isVisited: false,
    isWall: false,
    previousNode: null
  };
};

