import React, {Component} from 'react';
import Visualizer from './PathFindingVisualizer/Visualizer'
import {dijkstra, getNodesInShortestPathOrder} from '../algorithims/dijkstra';
import {aStar} from '../algorithims/aStar'
import { breadthFirstSearch } from '../algorithims/breadthFirstSearch';
import { greedyBFS } from '../algorithims/greedyBestFirstSearch';
import { depthFirstSearch } from '../algorithims/depthFisrtSearch';
import { randomMaze } from '../mazeAlgorithims/randomMaze';
import { verticalMaze } from '../mazeAlgorithims/verticalMaze';
import { horizontalMaze } from '../mazeAlgorithims/HorizontalMaze';
import { recursiveDivisionMaze } from '../mazeAlgorithims/recursiveDivisionMaze';
import { bidirectionalGreedySearch } from '../algorithims/bidirectionalGreedySearch';
import { getNodesInShortestPathOrderBidirectionalGreedySearch } from '../algorithims/bidirectionalGreedySearch';
import Navbar from './navComponents/Navbar';
import itemData from '../data/item-data'
import algData from '../data/alg-data'
import './mainContainer.css'
import { ContactlessOutlined } from '@material-ui/icons';

// const START_NODE_ROW = 10;
// const START_NODE_COL = 5;
// const FINISH_NODE_ROW = 10;
// const FINISH_NODE_COL = 45;

export default class MainContainer extends Component{
    constructor(props){
        super(props);
        this.state = {
            grid: [],
            mouseIsPressed: false,
            showNav: false,
            currItemDesc: 'Start',
            currItemObj: itemData[0],
            currAlgObj: algData[0],
            currAlgTab: 0,
            currMazeAlg: 'Recursive Division Maze',
            currMazeTab: 0,
            startNode: [10, 5],
            finishNode: [10, 45],
            startIsMoving: false,
            finishIsMoving: false,
            isThereStop: false,
            stopIsMoving: false,
            prevStop: [],
            generatingMaze: false,
            visualizingAlgorithm: false
        }
    }

    componentDidMount() {
        const grid = renderGrid(this.state);
        this.setState({grid});
    }

    handleMouseDown = (row, col) => {
        var newGrid = this.state.grid
        if(this.state.grid[row][col].isStart){
          this.setState({startIsMoving: true})
          newGrid = this.state.grid
        } else if(this.state.grid[row][col].isFinish){
          this.setState({finishIsMoving: true})
          newGrid = this.state.grid
        } else if(this.state.currItemDesc === 'Wall') {
          newGrid = getNewGridWithWallToggled(this.state.grid, row, col);
        } else if(this.state.currItemDesc === 'Weight'){
          newGrid = getNewGridWithWeightToggled(this.state.grid, row, col);
        } else if(this.state.currItemDesc === 'Stop'){
          this.setState({stopIsMoving: true, isThereStop: true})
        }
        this.setState({grid: newGrid, mouseIsPressed: true});
    }

    handleMouseEnter = (row, col) => {
        if (!this.state.mouseIsPressed) return;
        var newGrid = this.state.grid;
        if(this.state.startIsMoving){
          newGrid = getNewGridWithStart(this.state.grid, row, col, this.state.startNode)
          this.setState({startNode: [row, col]})
        } 
        else if(this.state.finishIsMoving){
          newGrid = getNewGridWithFinish(this.state.grid, row, col, this.state.finishNode)
          this.setState({finishNode: [row, col]})
        } else if(this.state.currItemDesc === 'Wall') {
          newGrid = getNewGridWithWallToggled(this.state.grid, row, col, this.currItemDesc);
        } else if(this.state.currItemDesc === 'Weight'){
          newGrid = getNewGridWithWeightToggled(this.state.grid, row, col);
        }  else if(this.state.currItemDesc === 'Stop'){
          newGrid = getNewGridWithStopToggled(this.state.grid, row, col, this.state.prevStop);
          this.setState({prevStop: [row, col]})
        }
        this.setState({grid: newGrid});
    }

    handleMouseUp = (row, col) => {
        this.setState({mouseIsPressed: false, startIsMoving: false, finishIsMoving: false});
    }

    handleNav = () => {
      this.setState({showNav: !this.state.showNav})
    }

    clearGrid() {
      if (this.state.visualizingAlgorithm || this.state.generatingMaze) {
        return;
      }
      const startNode = this.state.startNode
      const finishNode = this.state.finishNode
      for (let row = 0; row < this.state.grid.length; row++) {
        for (let col = 0; col < this.state.grid[0].length; col++) {
          if (
            !(
              (row === startNode[0] && col === startNode[1]) ||
              (row === finishNode[0] && col === finishNode[1])
            )
          ) {
            document.getElementById(`node-${row}-${col}`).className = "node";
          }
        }
      }
      var rowNum = (window.innerHeight) / 25
      var colNum = (window.innerWidth-20) / 25
      const newGrid = getInitialGrid(rowNum, colNum);
      this.setState({
        grid: newGrid,
        visualizingAlgorithm: false,
        generatingMaze: false,
      });
    }

    //navBar functions
    handleItemDescChange = (e, item) => {
      var newItem = `${item}`
      itemData.map((_item) => {
        if(item === _item.id){this.setState({currItemDesc: newItem, currItemObj: _item})}
      })
    }

    handleTabs = (e, val) => {
      this.setState({currAlgTab: val, currAlgObj: algData[val]})
    }

    handleMazeTabs = (e, val) => {
      this.setState({currMazeTab: val})
      console.log(val)
    }

    generateMaze = (e) => {
      var currAlg = this.state.currMazeTab

      if(currAlg===0){
        this.generateRecursiveDivisionMaze()
        console.log('hi')
      } else if(currAlg===1){
        this.generateVerticalMaze()
      } else if(currAlg===2){
        this.generateHorizontalMaze()
      } else if(currAlg===3){
        this.generateRandomMaze()
      }
    }

    // play animation

    playAnimation = (e) => {
      var currAlg = this.state.currAlgObj.id
      if(currAlg===0){
        this.visualizeDijkstra()
      } else if(currAlg===1){
        this.visualizeaStar()
      } else if(currAlg===6){
        this.visualizbfs()
      } else if(currAlg===2){
        this.visualizegbfs()
      } else if(currAlg===7){
        this.visualizegdfs()
      } else if(currAlg===5){
        this.visualizeBidirectionalGreedySearch()
      }
      return
    }

    //animte dijkstra

    animateDijkstra(visitedNodesInOrder, visitedNodesInOrderStop, nodesInShortestPathOrder, isStop) {
      console.log(visitedNodesInOrder)
      // for(let j = 0; j < 2; j++){
      //   for (let i = 0; i <= visitedNodesInOrder[j].length; i++) {
      //     if (i === visitedNodesInOrder[j].length) {
      //       if(isStop===true){break}
      //       setTimeout(() => {
      //           this.animateShortestPath(nodesInShortestPathOrder);
      //       }, 10 * i);
      //       return;
      //     } 
      //     setTimeout(() => {
      //       const node = visitedNodesInOrder[j][i];
      //       console.log(node)
      //       document.getElementById(`node-${node.row}-${node.col}`).className =
      //       isStop ? 'node node-target-visited' : 'node node-visited';
      //     }, 10 * i);
      //   }
      // }
      for (let i = 0; i <= visitedNodesInOrder.length; i++) {
        if (i === visitedNodesInOrder.length && !isStop) {
          setTimeout(() => {
            this.animateShortestPath(nodesInShortestPathOrder);
          }, 10 * i);
          return;
        }
        if(i === visitedNodesInOrder.length && isStop){
          console.log('here')
          for (let j = 0; j <= visitedNodesInOrderStop.length; j++) {
            if (j === visitedNodesInOrderStop.length) {
              setTimeout(() => {
                this.animateShortestPath(nodesInShortestPathOrder);
              }, 10 * j);
              return;
            }
            setTimeout(() => {
              const node = visitedNodesInOrderStop[j];
              document.getElementById(`node-${node.row}-${node.col}`).className =
                'node node-target-visited';
            }, 10 * j);
          }
        }
        setTimeout(() => {
          const node = visitedNodesInOrder[i];
          document.getElementById(`node-${node.row}-${node.col}`).className =
            'node node-visited';
        }, 10 * i);
      }
    }

    visualizeDijkstra = () => {
          const {grid} = this.state;
          const startNode = this.state.startNode
          const finishNode = this.state.finishNode
          const stopNode = this.state.prevStop ? this.state.prevStop : []
          const _startNode = grid[startNode[0]][startNode[1]];
          const _finishNode = grid[finishNode[0]][finishNode[1]]
          const _stopNode = this.state.isThereStop ? grid[stopNode[0]][stopNode[1]] : false;
          var visitedNodesInOrder = []
          var visitedNodesInOrderStop = []
          if(_stopNode === false){
            visitedNodesInOrder = dijkstra(grid, _startNode, _finishNode);
          } else if(_stopNode != false){
            visitedNodesInOrder = dijkstra(grid, _startNode, _stopNode)
            var _newGrid = renderGrid(this.state)
            visitedNodesInOrderStop = dijkstra(_newGrid, _stopNode, _finishNode)
          }
          console.log(visitedNodesInOrder, visitedNodesInOrderStop)
          const nodesInShortestPathOrder = getNodesInShortestPathOrder(_finishNode);
          this.animateDijkstra(visitedNodesInOrder, visitedNodesInOrderStop, nodesInShortestPathOrder, this.state.isThereStop);
    }

    //animate aStar

    animateaStar(visitedNodesInOrder, nodesInShortestPathOrder) {
      for (let i = 0; i <= visitedNodesInOrder.length; i++) {
        if (i === visitedNodesInOrder.length) {
          setTimeout(() => {
              this.animateShortestPath(nodesInShortestPathOrder);
          }, 10 * i);
          return;
          }
        setTimeout(() => {
          const node = visitedNodesInOrder[i];
          document.getElementById(`node-${node.row}-${node.col}`).className =
            'node node-visited';
          }, 10 * i);
      }
    }

    visualizeaStar() {
      const {grid} = this.state;
      const startNode = this.state.startNode
      const finishNode = this.state.finishNode
      const _startNode = grid[startNode[0]][startNode[1]];
      const _finishNode = grid[finishNode[0]][finishNode[1]];
      const visitedNodesInOrder = aStar(grid, _startNode, _finishNode);
      const nodesInShortestPathOrder = getNodesInShortestPathOrder(_finishNode);
      this.animateaStar(visitedNodesInOrder, nodesInShortestPathOrder);
    }

    //animate breadth first search 

    animatebfs(visitedNodesInOrder, nodesInShortestPathOrder) {
      for (let i = 0; i <= visitedNodesInOrder.length; i++) {
        if (i === visitedNodesInOrder.length) {
          setTimeout(() => {
              this.animateShortestPath(nodesInShortestPathOrder);
          }, 10 * i);
          return;
          }
        setTimeout(() => {
          const node = visitedNodesInOrder[i];
          document.getElementById(`node-${node.row}-${node.col}`).className =
            'node node-visited';
          }, 10 * i);
      }
    }

    visualizbfs() {
      const {grid} = this.state;
      const startNode = this.state.startNode
      const finishNode = this.state.finishNode
      const _startNode = grid[startNode[0]][startNode[1]];
      const _finishNode = grid[finishNode[0]][finishNode[1]];
      const visitedNodesInOrder = breadthFirstSearch(grid, _startNode, _finishNode);
      const nodesInShortestPathOrder = getNodesInShortestPathOrder(_finishNode);
      this.animatebfs(visitedNodesInOrder, nodesInShortestPathOrder);
    }

    // greedy best first search

    animategbfs(visitedNodesInOrder, nodesInShortestPathOrder) {
      for (let i = 0; i <= visitedNodesInOrder.length; i++) {
        if (i === visitedNodesInOrder.length) {
          setTimeout(() => {
              this.animateShortestPath(nodesInShortestPathOrder);
          }, 10 * i);
          return;
          }
        setTimeout(() => {
          const node = visitedNodesInOrder[i];
          document.getElementById(`node-${node.row}-${node.col}`).className =
            'node node-visited';
          }, 10 * i);
      }
    }

    visualizegbfs() {
      const {grid} = this.state;
      const startNode = this.state.startNode
      const finishNode = this.state.finishNode
      const _startNode = grid[startNode[0]][startNode[1]];
      const _finishNode = grid[finishNode[0]][finishNode[1]];
      const visitedNodesInOrder = greedyBFS(grid, _startNode, _finishNode)
      const nodesInShortestPathOrder = getNodesInShortestPathOrder(_finishNode)
      this.animategbfs(visitedNodesInOrder, nodesInShortestPathOrder)
    }

    // depth first search

    animatedfs(visitedNodesInOrder, nodesInShortestPathOrder) {
      for (let i = 0; i <= visitedNodesInOrder.length; i++) {
        if (i === visitedNodesInOrder.length) {
          setTimeout(() => {
              this.animateShortestPath(nodesInShortestPathOrder);
          }, 10 * i);
          return;
          }
        setTimeout(() => {
          const node = visitedNodesInOrder[i];
          document.getElementById(`node-${node.row}-${node.col}`).className =
            'node node-visited';
          }, 10 * i);
      }
    }

    visualizegdfs() {
      const {grid} = this.state;
      const startNode = this.state.startNode
      const finishNode = this.state.finishNode
      const _startNode = grid[startNode[0]][startNode[1]];
      const _finishNode = grid[finishNode[0]][finishNode[1]];
      const visitedNodesInOrder = depthFirstSearch(grid, _startNode, _finishNode)
      const nodesInShortestPathOrder = getNodesInShortestPathOrder(_finishNode)
      this.animategbfs(visitedNodesInOrder, nodesInShortestPathOrder)
    }

    // bidirectional greedy first search

    animateBidirectionalAlgorithm(
      visitedNodesInOrderStart,
      visitedNodesInOrderFinish,
      nodesInShortestPathOrder,
      isShortedPath
    ) {
      let len = Math.max(
        visitedNodesInOrderStart.length,
        visitedNodesInOrderFinish.length
      );
      for (let i = 1; i <= len; i++) {
        let nodeA = visitedNodesInOrderStart[i];
        let nodeB = visitedNodesInOrderFinish[i];
        if (i === visitedNodesInOrderStart.length) {
          setTimeout(() => {
            let visitedNodesInOrder = getVisitedNodesInOrder(
              visitedNodesInOrderStart,
              visitedNodesInOrderFinish
            );
            if (isShortedPath) {
              this.animateShortestPath(
                nodesInShortestPathOrder,
                visitedNodesInOrder
              );
            } else {
              this.setState({ visualizingAlgorithm: false });
            }
          }, i * 10);
          return;
        }
        setTimeout(() => {
          //visited nodes
          if (nodeA !== undefined)
            document.getElementById(`node-${nodeA.row}-${nodeA.col}`).className =
              "node node-visited";
          if (nodeB !== undefined)
            document.getElementById(`node-${nodeB.row}-${nodeB.col}`).className =
              "node node-visited";
        }, i * 10);
      }
    }

    visualizeBidirectionalGreedySearch() {
      if (this.state.visualizingAlgorithm || this.state.generatingMaze) {
        return;
      }
      this.setState({ visualizingAlgorithm: true });
      setTimeout(() => {
        const { grid } = this.state;
        const _startNode = this.state.startNode
        const _finishNode = this.state.finishNode
        const startNode = grid[_startNode[0]][_startNode[1]];
        const finishNode = grid[_finishNode[0]][_finishNode[1]];
        const visitedNodesInOrder = bidirectionalGreedySearch(
          grid,
          startNode,
          finishNode
        );
        const visitedNodesInOrderStart = visitedNodesInOrder[0];
        const visitedNodesInOrderFinish = visitedNodesInOrder[1];
        const isShortedPath = visitedNodesInOrder[2];
        const nodesInShortestPathOrder = getNodesInShortestPathOrderBidirectionalGreedySearch(
          visitedNodesInOrderStart[visitedNodesInOrderStart.length - 1],
          visitedNodesInOrderFinish[visitedNodesInOrderFinish.length - 1]
        );
        this.animateBidirectionalAlgorithm(
          visitedNodesInOrderStart,
          visitedNodesInOrderFinish,
          nodesInShortestPathOrder,
          isShortedPath
        );
      }, 10);
    }

    //finish animations

    animateShortestPath(nodesInShortestPathOrder) {
      for (let i = 0; i < nodesInShortestPathOrder.length; i++) {
        setTimeout(() => {
          const node = nodesInShortestPathOrder[i];
          document.getElementById(`node-${node.row}-${node.col}`).className =
            'node node-shortest-path';
        }, 50 * i);
      }
    }

    //generate mazes

    animateMaze = (walls) => {
      for (let i = 0; i <= walls.length; i++) {
        if (i === walls.length) {
          setTimeout(() => {
            this.clearGrid();
            let newGrid = getNewGridWithMaze(this.state.grid, walls);
            this.setState({ grid: newGrid, generatingMaze: false });
          }, i * 10);
          return;
        }
        let wall = walls[i];
        let node = this.state.grid[wall[0]][wall[1]];
        setTimeout(() => {
          //Walls
          document.getElementById(`node-${node.row}-${node.col}`).className =
            "node node-wall";
        }, i * 10);
      }
    };

    generateRandomMaze() {
      if (this.state.visualizingAlgorithm || this.state.generatingMaze) {
        return;
      }
      this.setState({ generatingMaze: true });
      setTimeout(() => {
        const { grid } = this.state;
        const _startNode = this.state.startNode
        const _finishNode = this.state.finishNode
        const startNode = grid[_startNode[0]][_startNode[1]]
        const finishNode = grid[_finishNode[0]][_finishNode[1]]
        const walls = randomMaze(grid, startNode, finishNode);
        this.animateMaze(walls);
      }, 10);
    }

    generateRecursiveDivisionMaze() {
      if (this.state.visualizingAlgorithm || this.state.generatingMaze) {
        return;
      }
      this.setState({ generatingMaze: true });
      setTimeout(() => {
        const { grid } = this.state;
        const _startNode = this.state.startNode
        const _finishNode = this.state.finishNode
        const startNode = grid[_startNode[0]][_startNode[1]]
        const finishNode = grid[_finishNode[0]][_finishNode[1]]
        const walls = recursiveDivisionMaze(grid, startNode, finishNode);
        this.animateMaze(walls);
      }, 10);
    }

    generateVerticalMaze() {
      if (this.state.visualizingAlgorithm || this.state.generatingMaze) {
        return;
      }
      this.setState({ generatingMaze: true });
      setTimeout(() => {
        const { grid } = this.state;
        const _startNode = this.state.startNode
        const _finishNode = this.state.finishNode
        const startNode = grid[_startNode[0]][_startNode[1]]
        const finishNode = grid[_finishNode[0]][_finishNode[1]]
        const walls = verticalMaze(grid, startNode, finishNode);
        this.animateMaze(walls);
      }, 10);
    }

    generateHorizontalMaze() {
      if (this.state.visualizingAlgorithm || this.state.generatingMaze) {
        return;
      }
      this.setState({ generatingMaze: true });
      setTimeout(() => {
        const { grid } = this.state;
        const _startNode = this.state.startNode
        const _finishNode = this.state.finishNode
        const startNode = grid[_startNode[0]][_startNode[1]]
        const finishNode = grid[_finishNode[0]][_finishNode[1]]
        const walls = horizontalMaze(grid, startNode, finishNode);
        this.animateMaze(walls);
      }, 10);
    }

    render(){ 
        return(
            <div className="main-container">
                <button onClick={this.handleNav}>show nav</button>
                <div className="visualizer-container">
                    <Visualizer 
                        state={this.state} 
                        handleMouseDown={this.handleMouseDown} 
                        handleMouseEnter={this.handleMouseEnter} 
                        handleMouseUp={this.handleMouseUp}
                    />
                </div>
                <Navbar state={this.state} handleMazeTabs={this.handleMazeTabs} genMaze={this.generateMaze} play={this.playAnimation} handleTabs={this.handleTabs} handleItemChange={this.handleItemDescChange} visualizeDijkstra={this.visualizeDijkstra}/>
            </div>
        )
    }
}

const renderGrid = (state) => {
    const grid = [];
    var rowNum = (window.innerHeight-30) / 25
    var colNum = (window.innerWidth-20) / 25
    for (let row = 0; row < rowNum; row++) {
      const currentRow = [];
      for (let col = 0; col < colNum; col++) {
        currentRow.push(createNode(col, row, state));
      }
      grid.push(currentRow);
    }
    return grid;
}

const createNode = (col, row, state) => {
    const startNode = state.startNode
    const finishNode = state.finishNode
    const stopNode = state.prevStop
    return {
      col,
      row,
      isStart: row === startNode[0] && col === startNode[1],
      isFinish: row === finishNode[0] && col === finishNode[1],
      isStop: state.isThereStop ? row === stopNode[0] && col === stopNode[1] : null,
      distance: Infinity,
      isVisited: false,
      isWall: false,
      previousNode: null,
    };
  };

  const getNewGridWithStart = (grid, row, col, prevStart) => {
    const newGrid = grid.slice();
    const newStart = newGrid[row][col];
    const oldStart = newGrid[prevStart[0]][prevStart[1]]
    const oldNode = {
      ...oldStart,
      isStart: !oldStart.isStart
    }
    const newNode = {
      ...newStart,
      isStart: !newStart.isStart,
    };
    newGrid[row][col] = newNode;
    newGrid[prevStart[0]][prevStart[1]] = oldNode
    return newGrid;
  }

  const getNewGridWithFinish = (grid, row, col, prevFinish) => {
    const newGrid = grid.slice();
    if(newGrid[row][col].isStart || newGrid[row][col].isWall) return grid;
    const newFinish = newGrid[row][col];
    const oldFinish = newGrid[prevFinish[0]][prevFinish[1]]
    const oldNode = {
      ...oldFinish,
      isFinish: !oldFinish.isFinish
    }
    const newNode = {
      ...newFinish,
      isFinish: !newFinish.isFinish,
    };
    newGrid[row][col] = newNode;
    newGrid[prevFinish[0]][prevFinish[1]] = oldNode
    return newGrid;
  }

  const getNewGridWithWallToggled = (grid, row, col) => {
    const newGrid = grid.slice();
    const node = newGrid[row][col];
    var newNode = {...node}
    newNode = {
      ...node,
      isWall: !node.isWall,
    };
    newGrid[row][col] = newNode;
    return newGrid;
  };

  const getNewGridWithWeightToggled = (grid, row, col) => {
    const newGrid = grid.slice();
    const node = newGrid[row][col];
    var newNode = {...node}
    newNode = {
      ...node,
      isWeight: !node.isWeight,
    };
    newGrid[row][col] = newNode;
    return newGrid;
  };

  const getNewGridWithStopToggled = (grid, row, col, prevStop) => {
    const newGrid = grid.slice();
    const newStop = newGrid[row][col];
    if(prevStop.length === 0){
      const newNode = {
        ...newStop,
        isStop: !newStop.isStop,
      };
      newGrid[row][col] = newNode;
      return newGrid;
    }
    const oldStop = newGrid[prevStop[0]][prevStop[1]]
    const oldNode = {
      ...oldStop,
      isStop: !oldStop.isStop
    }
    const newNode = {
      ...newStop,
      isStop: !newStop.isStop,
    };
    newGrid[row][col] = newNode;
    newGrid[prevStop[0]][prevStop[1]] = oldNode
    return newGrid;
  };

  const getNewGridWithMaze = (grid, walls) => {
    let newGrid = grid.slice();
    for (let wall of walls) {
      let node = grid[wall[0]][wall[1]];
      let newNode = {
        ...node,
        isWall: true,
      };
      newGrid[wall[0]][wall[1]] = newNode;
    }
    return newGrid;
  };

  const getInitialGrid = (numRows, numColumns) => {
    let grid = [];
    for (let row = 0; row < numRows; row++) {
      let currentRow = [];
      for (let col = 0; col < numColumns; col++) {
        currentRow.push(createNode(row, col));
      }
      grid.push(currentRow);
    }
    return grid;
  };

  const getVisitedNodesInOrder = (
    visitedNodesInOrderStart,
    visitedNodesInOrderFinish
  ) => {
    let visitedNodesInOrder = [];
    let n = Math.max(
      visitedNodesInOrderStart.length,
      visitedNodesInOrderFinish.length
    );
    for (let i = 0; i < n; i++) {
      if (visitedNodesInOrderStart[i] !== undefined) {
        visitedNodesInOrder.push(visitedNodesInOrderStart[i]);
      }
      if (visitedNodesInOrderFinish[i] !== undefined) {
        visitedNodesInOrder.push(visitedNodesInOrderFinish[i]);
      }
    }
    return visitedNodesInOrder;
  };
